jQuery(document).ready(function( $ ) {

    /*--------------------------------------------------------------
    # Form send && validation
    --------------------------------------------------------------*/
    const formClassName = 'mbr-form';
    //https://wa.me/380668632827
    const waLink = $('#wa-link');

    $('.'+formClassName).submit(ajaxSubmit);
    function ajaxSubmit(e){


        var valid = false;
        var newCustomerForm = jQuery(this).serialize();

        let inpName = $(this).find('input[name="name"]');
        let inpPhone = $(this).find('input[name="phone"]');
        let succBox = $(this).find('.alert-success');

        if(inpName.val() !== '') {
            valid = true;

        }
        

        if(valid == true){

            //succBox.prop('hidden', false);

            
            jQuery.ajax({
                type:"POST",
                url: "mail.php",
                data: newCustomerForm,
                success:function(XHR){
                    //console.log('Письмо отправлено!');
                    //$('.main-form__success').show();
                    //setTimeout(() => {
                    //    $('.main-form__success').hide();
                    //}, 5000);
                    //$('.main-form__form .btn__text').html('Отправлено');
                    
                    inpName.val('');
                    inpPhone.val('');

                    window.location.href = "thank-yuo.html";
                    //window.location.href = "https://wa.me/380668632827";

                    setTimeout(() => {
                        succBox.prop('hidden', true);
                    }, 7000);
                }
            });	
        }
        
        return false;
    }        

});